from copy import copy
class Solution:
    def findLength(self, A, B):
        """
        :type A: List[int]
        :type B: List[int]
        :rtype: int
        """
        res = 0
        dp = [0] * (len(A)+1)
        for c1 in B:
            newdp = copy(dp)
            for i, c2 in enumerate(A):
                if c1 == c2 :
                    newdp[i+1] = dp[i]+1
                    res = max(res, newdp[i+1])
                else:
                    newdp[i+1] = 0
            dp = newdp
            # print(dp)
        return res

class Solution1:
    def findLength(self, A, B):
        
        def check(length):
            seen = {A[i:i+length]
                    for i in range(len(A) - length + 1)}
            return any(B[j:j+length] in seen
                       for j in range(len(B) - length + 1))

        A = ''.join(map(chr, A))
        B = ''.join(map(chr, B))
        lo, hi = 0, min(len(A), len(B)) + 1
        while lo+1 < hi:
            mi = (lo + hi) // 2
            if check(mi):
                lo = mi
            else:
                hi = mi
        return lo

